fn main() {
    let mut floor = 0i32;
    
    let mut basement_found = false;
    let mut first_basement = 0i32;

    for letter in INPUT.chars() {
        let to_add = match letter {
            '(' => 1,
            ')' => -1,
            _ => 0,
        };

        floor += to_add;

        if !basement_found {
            first_basement += 1;
        }

        if floor == -1 {
            basement_found = true;
        }
    }

    println!("floor #{:?}", floor);
    println!("first basement #{:?}", first_basement);
}


